import logo from './logo.svg';
import './App.css';
import React, { Component, useState } from 'react';

class WelcomeClass extends Component {
  constructor(props) {
    super(props);
    this.state = { greeting: 'Hello' };
  }

  render() {
    return <h1>{this.state.greeting}, {this.props.name}</h1>;
  }

}



function CounterFunc() {
  const [count, setCount] = useState(0);

  return (
    <div>
      <p>Count: {count}</p>
      <button onClick={() => setCount(count + 1)}>Increment</button>
    </div>
  );
}

class Counter extends Component {
  constructor (props) {
    super(props);
    this.state = { count: 0 };
  }

  incrementCount = () => {
    this.setState({ count: this.state.count + 1 });
  }

  decrementCount = () => {
    this.setState({ count: this.state.count - 1 });
  }

  render() {
    return (
      <div>
        <p>Count: {this.state.count}</p>
        <button onClick={this.incrementCount}>Increment</button>
        <button onClick={this.decrementCount}>Decrement</button>
      </div>
    );
  }
}

function handleClick() {
  console.log('Button clicked');
};

function Welcome(props) {
  return <h1>Hello, {props.name}</h1>;
};

function WelcomeMessage({ isLoggedIn }) {
  return (
    <div>
      {isLoggedIn ? <h1>Welcome back!</h1> : <h1>Please sign in.</h1>}
    </div>
  );
};

function App() {

  const element = <Welcome name="React Developer" />;
  const isLoggedIn = false;
  const items = ['Apple', 'Banana', 'Cherry'];

  const listItems = items.map((item) => <li key={item}>{item}</li>);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <p>
          Today's date: {new Date().toLocaleDateString()}
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
        <button onClick={handleClick}>CLICK THIS BUTTON TO PRINT TO CONSOLE</button>
        <h1>{element}</h1>
        {WelcomeMessage({isLoggedIn})}
        <WelcomeClass name="DOUG" />

        <ul>{listItems}</ul>
        <Counter />
        {CounterFunc()}

      </header>
    </div>
  );
}

export default App;
